import React ,{Component}from 'react';
import './lists.scss';

import {Link} from 'react-router-dom';
import {connect} from 'react-redux';
import * as types from '../../../redux/const';
import {asyActionLists} from '../../../redux/asyncAction';
class Lists extends Component{
    componentDidMount(){
       let id =this.props.match.params.id;
        console.log(this.props.match.params.id);
        this.props.getLists(id);
        console.log(this.props.getLists.lists);
        console.log()
    }
    render(){
        let {lists}=this.props;
        console.log(this.props.lists);
        return(
            <div className="lists">

             <div className="lists-header">
              <div className="left-box">
              <i className="fa fa-left" ></i>
              </div>
               热卖爆款
             </div>

             <div className="list-content">
               <ul className="content-banner">
               <li>销量</li>  
               <li>新品</li>
               <li>价格</li>
               </ul>
            <div className="content-main">
             <ul className="list-main">
             {
                this.props.lists.map(item=>(

                  <li>
                  <a href="javascript:;">
                  <img src={item.imgsrc}/>
                  <p className="list-name">{item.pro_name}</p>
                  <h4>{item.pro_name}</h4>
                  <p className="list-price">{item.sprice}</p>
                  </a>
                  <div className="list-car"><i className="fa fa-shopping-cart"></i></div>
              </li>    
                ))
             
             }
             
             </ul>


            </div>
             </div>

               
            </div>
        )
    }
}

let MapStateToProps=(state)=>(
    {
      lists:state.lists
    }
);
let MapDispatchToProps=(dispatch)=>(
{
 getLists:(id)=>dispatch(asyActionLists(
         '/detail.json',
          types.UPDATE_LISTS,
          dispatch,id

 ))
})


 
export default connect(

    MapStateToProps,
    MapDispatchToProps
)(Lists);
